import React from 'react'

import '../../pages/Login/login'


const handleSubmitCrear = (e) => {
    e.preventDefault();

    try 
    {
        const id = document.getElementById('documento').value;
        const nombre_usuario = document.getElementById('nombre').value;
        const tipo_documento = document.getElementById('tipoD').value;
        const sexo_usuario = document.getElementById('sexo').value;
        const nacionalidad_usuario = document.getElementById('nacionalidad').value;
        const telefono_usuario = document.getElementById('telefono').value;
        const direccion_usuario = document.getElementById('direccion').value;
        const clave_usuario = document.getElementById('contrasena').value;
        const clave_usuario2 = document.getElementById('contrasena2').value;
    } 
    catch (e)
    {
        alert("Por Favor llene todos los campos")
    }

    console.log(id);
}


export default function userCreate (){

    const   url = window.location.href;
    console.log(url);

    return (
        <>
            <h2 className='titulo'>Formulario de ingreso de usuarios</h2>
            <form className="row g-3 formulario" >

                <label className="form-label col-sm-2 labelForm">Numero de documento</label>
                <div className="col-md-10 listaOpciones">
                    <input type="number" className="form-control" id="documento" placeholder="Nro de documento" required/>
                </div>

                
                <label className="form-label col-sm-2 labelForm">Nombres</label>
                <div className="col-md-10">
                    <input type="text" className="form-control" id="nombre" placeholder="Nombre completo" required/>
                </div>

                
                <label className="form-label col-sm-2 labelForm">Tipo de documento</label>
                <div className="col-md-10 listaOpciones">
                    <select className="form-select" id="tipoD" required>
                        <option value='CC'>Cedula de Ciudadania</option>
                        <option value='TI'>Tarjeta de Identidad</option>
                    </select>
                </div>

                
                <label className="form-label col-sm-2 labelForm">Sexo</label>
                <div className="col-md-10 listaOpciones">
                    <select className="form-select" id="sexo" required>
                        <option value="M">Masculino</option>
                        <option value="F">Femenino</option>
                        <option value="O">Otro</option>
                    </select>
                </div>

                
                <label className="form-label col-sm-2 labelForm">Telefono</label>
                <div className="col-md-10 listaOpciones">
                    <input type="number" className="form-control" id="telefono"  placeholder="Telefono" required/>
                </div>

                
                <label htmlFor="validationDefault03" className="form-label col-sm-2 labelForm">Direccion</label>
                <div className="col-md-10 ">
                    <input type="text" className="form-control" id="direccion" placeholder="Direccion" required/>
                </div>

                
                <label className="form-label col-sm-2 labelForm">Nacionalidad</label>
                <div className="col-md-10 listaOpciones">
                    <select className="form-select" id="nacionalidad" required>
                        <option value="Colombia">Colombiano(a)</option>
                        <option value="Extranjero">Extranjero(a)</option>
                    </select>
                </div>

                
                
                <label htmlFor="validationDefault03" type = "password" className="form-label col-sm-2 labelForm">Crea Contraseña</label>
                <div className="col-md-10">
                    <input type="password" className="form-control" id="direccion" placeholder="Contrasena"/>
                </div>

                
                
                <label htmlFor="validationDefault03"  className="form-label col-sm-2 labelForm">Repite Contreseña</label>
                <div className="col-md-10">
                    <input type="password" className="form-control" id="direccion" placeholder="Contrasena2"/>
                </div>
                <div className="col-12 divBoton">
                {
                    url.includes('crear/usuario') ?
                    <button className="btn btn-primary boton " type="submit"  onClickCapture={handleSubmitCrear}>Crear Usuario</button>
                    :<button className="btn btn-primary boton " type="submit"  >Actualizar Datos</button>
                }
                </div>

            </form>
        </>
    );
}
