import React, {useState} from 'react'
import {useHistory} from 'react-router-dom'
import {Helmet} from 'react-helmet';
import { isElementOfType } from 'react-dom/test-utils';
//import LeerGasolinera from './leerSede'

var data = [];

export default function AsignarMembresia() {
    
    const [id, setId_usuario] = useState("");
    const navigate = useHistory();
    const valores = JSON.parse(sessionStorage.getItem('login'));
    const token = valores.datos.token;
    const userId = valores.datos.id;
    const f = new Date();
    console.log(valores)

    const HandleSubmitBuscar = (e) =>
    {
        e.preventDefault();

        const data = {

            ESTADO: 1,
            id_membresia: document.getElementById('membresia').value,
            id_usuario: userId,
            fecha_compra: (f.getFullYear() + "/" + (f.getMonth()+1) + "/" + f.getDate()),
            fecha_activacion: (f.getFullYear() + "/" + (f.getMonth()+1) + "/" + f.getDate()),
            t001_usuarios_id_usuario: userId,
            t004_membresias_id_membresia: document.getElementById('membresia').value,
        };
        
        fetch('http://localhost:5000/membresias/asignar', {
            method: 'POST',
            body: JSON.stringify(data),
            headers:{
                'Content-Type': 'application/json',
                'x-access-token': token
            }
        }).then ((respuesta) =>{ 
            respuesta.json().then((result) => {
                console.log(result)
                if(result.Auth)
                {
                    alert(result.done)
                        sessionStorage.setItem('membresia', JSON.stringify({
                        datos: result.datos
                    }));
                    navigate.push('/dashboard/overview')
                }
                else
                {
                    alert(result.done)
                }
            })
        });
    }

    return(
        <div className="wrapper fadeInDown">
            <div id="formContent" className="editarUsuario">
                <Helmet>
                    <title>Membresia</title>
                </Helmet>
                <h2 className='titulo'>Buscar Membresia</h2>
                <form className="row g-3 formulario" autoComplete="off" novalidate>
                <div className="col-md-8">
                    <input onChange={(e) => setId_usuario(e.target.value)} value={id} 
                        type="number" className="form-control" id="membresia" placeholder="Ingresar el id" required/>
                    </div>
                    <button className="btn btn-primary  col-sm-1" type="submit"  onClickCapture={HandleSubmitBuscar} ><i className="fas fa-search"></i></button>
                </form>
            </div>
        </div>
    );

}
